    public static final String CAMEL_SOURCE_DEBEZIUMMYSQL_COMPONENT_MESSAGE_KEY_COLUMNS_CONF = "camel.component.debezium-mysql.messageKeyColumns";
    private static final String CAMEL_SOURCE_DEBEZIUMMYSQL_COMPONENT_MESSAGE_KEY_COLUMNS_DOC = "A semicolon-separated list of expressions that match fully-qualified tables and column(s) to be used as message key. Each expression must match the pattern ':',where the table names could be defined as (DB_NAME.TABLE_NAME) or (SCHEMA_NAME.TABLE_NAME), depending on the specific connector,and the key columns are a comma-separated list of columns representing the custom key. For any table without an explicit key configuration the table's primary key column(s) will be used as message key.Example: dbserver1.inventory.orderlines:orderId,orderLineId;dbserver1.inventory.orders:id";
    private static final String CAMEL_SOURCE_DEBEZIUMMYSQL_COMPONENT_MESSAGE_KEY_COLUMNS_DEFAULT = null;
    public static final String CAMEL_SOURCE_DEBEZIUMMYSQL_COMPONENT_POLL_INTERVAL_MS_CONF = "camel.component.debezium-mysql.pollIntervalMs";
    private static final String CAMEL_SOURCE_DEBEZIUMMYSQL_COMPONENT_POLL_INTERVAL_MS_DOC = "Frequency in milliseconds to wait for new change events to appear after receiving no events. Defaults to 500ms.";
